/**
 * Populate the countryCode dropdown with options
 */
const populateCountryDropdown = () => {
    console.log("START country Code")
    countryData.forEach(country => {
        const opt = document.createElement('option');
        opt.setAttribute('data-country', country.code);
        opt.value = country.dial_code;
        opt.innerHTML = `${country.flag_code} ${country.name} (${country.dial_code})`;
        countryCode.appendChild(opt);
    });
};

/**
 * Set default country in the select element based on country code
 */
const setDefaultCountry = (selectElement, countryCodeValue) => {
    for (let i = 0; i < selectElement.options.length; i++) {
        if (selectElement.options[i].getAttribute('data-country').toLowerCase() === countryCodeValue.toLowerCase()) {
            selectElement.selectedIndex = i;
            countryCodeDisplay.innerText = selectElement.options[i].value; // Update display
            break;
        }
    }
};

/**
 * Update the display when the country code changes
 */
const updateCountryCodeDisplay = (event) => {
    countryCodeDisplay.innerText = event.target.value;
};

/**
 * Initialize the form
 */
const start = () => {
    if (countryCode) { // Only run if countryCode dropdown exists
        setFieldFocus("phone")
        populateCountryDropdown();

        // Set the default country or fallback to the United States ("us")
        if (defaultCountry) {
            setDefaultCountry(countryCode, defaultCountry);
        } else {
            setDefaultCountry(countryCode, "us");
        }

        // Add event listener for when the dropdown value changes
        countryCode.addEventListener("change", updateCountryCodeDisplay);
    }

    // Start WebAuthN registration if the `fidoRetry` button exists (for retry functionality)
    if (fidoButton) {
        fidoButton.addEventListener("click", startFido);
        // const fidoButton = document.getElementById("fidoRetry");
        console.log("FIDO BUTTON", fidoButton)
        // fidoButton.addEventListener("click", startFido);
        // fidoButton.click();
        // Run WebAuthN registration immediately
        startFido();
    }
    if (emailField) {
        setFieldFocus("email")
    } else {
        setFieldFocus("passcode")
    }
};

/**
 * Focus an element for the provider id
 */
const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    }
}

// Ensure the DOM is fully loaded before running the start function
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", start);
} else {
    start();
}

