// Main entry point
module.exports = a = async ({ params }) => {

	// Function to get the ID for a given device type
	function getDeviceId(deviceId) {
		// If no deviceType is provided, default to the first device
		if (!deviceId) {
			return params.listOfDevices[0] || { message: 'No devices available' };
		}

		// Find the device with the matching type
		const device = (params.listOfDevices).find(d => d.type === deviceId);

		// Return the entire device object if found, otherwise return a not found message
		if (device) {
			return device;
		} else {
			return { message: 'Device type not found' };
		}
	}

	return { "backUpInfo": getDeviceId(params.buttonValue) };
}

