// Write your code here
// Supported language: Javascript 
module.exports = a = async ({params}) => {
    // Main function
    // 1. Extract the max allowed devices and current devices from the rawResponse
    const maxAllowedDevices = params.deviceObject._embedded.mfaSettings.pairing.maxAllowedDevices;
    const registeredDevices = params.deviceObject._embedded.devices || [];

    // 2. Set already registered mandatory devices to mandatory: false
    const registeredTypes = new Set(registeredDevices.map(device => device.type));
    params.deviceList.forEach(device => {
        if (device.mandatory && registeredTypes.has(device.type)) {
            device.mandatory = false;
        }
    });

    // 3. Calculate if space is available for remaining mandatory devices
    const remainingSlots = maxAllowedDevices - registeredDevices.length;
    const remainingMandatoryDevices = params.deviceList.filter(device => device.mandatory);

    // 4. Filter usable devices to ensure mandatory devices fit if close to max allowed
    let usableDevices;
    if (remainingMandatoryDevices.length <= remainingSlots) {
        // Enough slots available, include all devices
        usableDevices = params.deviceList;
    } else {
        // Not enough slots, only include remaining mandatory devices
        usableDevices = remainingMandatoryDevices;
    }

    // 5. Check if any mandatory devices remain to be registered
    const mandatoryDevicesStillRemaining = remainingMandatoryDevices.length > 0;

     // 6. Determine if user can exit based on maxAllowedReached and remaining mandatory devices
    const maxAllowedReached = registeredDevices.length >= maxAllowedDevices;
    const canUserExit = maxAllowedReached || !mandatoryDevicesStillRemaining;

    // 7. Generate a comma-delimited list of registered device types
    const registeredDeviceTypesList = registeredDevices.map(device => device.type).join(',');


    return {
        usableDevices,
        remainingMandatoryDevices,
        maxAllowedReached,
        mandatoryDevicesStillRemaining,
        canUserExit,
        registeredDeviceTypesList
    };
}

