// -----------------------------------------------------------------------------
// Device type definitions for UI display
// Each device type maps to a user-friendly title and Material Design icon
// -----------------------------------------------------------------------------
const deviceTypes = {
    SMS: { title: "Text Message", icon: "mdi-comment-text-outline", status:"OTP_REQUIRED" },
    VOICE: { title: "Voice", icon: "mdi-face-agent", status:"OTP_REQUIRED" },
    EMAIL: { title: "Email", icon: "mdi-email-outline", status:"OTP_REQUIRED" },
    TOTP: { title: "Authenticator App", icon: "mdi-cellphone-key", status:"OTP_REQUIRED" },
    FIDO2: { title: "Passkey/FIDO2", icon: "mdi-fingerprint", status:"ASSERTION_REQUIRED" },
    PLATFORM: { title: "Biometrics", icon: "mdi-fingerprint", status:"ASSERTION_REQUIRED" },
    MOBILE: { title: "Mobile Push", icon: "mdi-account-key-outline", status:"PUSH_CONFIRMATION_REQUIRED" },
    PINGID_MOBILE: { title: "PingID App", icon: "mdi-cellphone", status:"PUSH_CONFIRMATION_REQUIRED" },
    SECURITY_KEY: { title: "Security Key", icon: "mdi-fingerprint", status:"ASSERTION_REQUIRED" },
    MAGIC: { title: "Magic Link", icon: "mdi-auto-fix", status:"OTP_REQUIRED" },
    PINGID_DESKTOP: { title: "Desktop App", icon: "mdi-auto-fix", status:"OTP_REQUIRED" },
    YUBIKEY: { title: "YubiKey", icon: "mdi-usb-flash-drive", status:"OTP_REQUIRED" },

    // Aggregated FIDO device map
    AGGREGATED_FIDO: { title: "Passkey", icon: "mdi-fingerprint", nickname: "Any Passkey", status:"ASSERTION_REQUIRED" }
};

// Constants for reuse and logic branching
const SMS = "SMS";
const VOICE = "VOICE";
const EMAIL = "EMAIL";
const SEPARATOR = " - ";
const MAGIC = "Magic Link";

// ----------------------------------------------------------------------------
// Helper: Mask email address (e.g., j***e@example.com)
// ----------------------------------------------------------------------------
const maskEmail = (email) => {
    const [username, domain] = email.split("@");
    const maskedUsername =
        username.charAt(0) + "*".repeat(username.length - 2) + username.slice(-1);
    return maskedUsername + "@" + domain;
};

// ----------------------------------------------------------------------------
// Helper: Mask phone number (e.g., ***-***-1234)
// ----------------------------------------------------------------------------
const maskPhone = (phone) => {
    const numericPhoneNumber = phone.replace(/[^\d+]/g, "");
    return "***-***-" + numericPhoneNumber.slice(-4);
};

// ----------------------------------------------------------------------------
// Main module function: Filters, prepares, and formats usable devices
// ----------------------------------------------------------------------------
module.exports = async ({ params }) => {
    console.log('params: ', params);

    let devices = params.devices;
    const passwordlessAuth = params.passwordlessAuth;
    const allowedTypes = params.passwordlessAllowedTypes || [];
    const fidoAggregation = params.fidoAggregation; // New parameter for FIDO aggregation
    const selectedDeviceId = params.selectedDeviceId; // Selected device passed as parameter

    // Toggle to control display of masked vs. unmasked values
    const showUnmasked = params.showUnmasked === true;

    // Handle edge case: no devices passed in
    if (!devices || devices.length === 0) {
        return {
            usableDevices: [],
            count: 0,
            canChangeDevice: false,
            askToRegister: true
        };
    }

    // Skip type filtering if allowedTypes is not set, or explicitly includes MFA/ALL
    const skipFilter =
        allowedTypes.length === 0 ||
        allowedTypes.includes("MFA") ||
        allowedTypes.includes("ALL");

    // Filter devices by allowedTypes (if filtering is not skipped)
    if (!skipFilter) {
        devices = devices.filter(d => allowedTypes.includes(d.type));
        if (devices.length === 0) {
            return {
                usableDevices: [],
                count: 0,
                canChangeDevice: false,
                askToRegister: true
            };
        }
    }

    // Filter WebAuthn devices based on support level
    if (params.webAuthNSupport === "NONE") {
        devices = devices.filter(device =>
            !["SECURITY_KEY", "PLATFORM", "FIDO2"].includes(device.type)
        );
    } else if (params.webAuthNSupport === "SECURITY_KEY_ONLY") {
        devices = devices.filter(device =>
            device.type !== "PLATFORM" && device.type !== "FIDO2"
        );
    }

    // Exclude devices that are in cooldown state
    devices = devices.filter(device => device.coolDownExpiresAt == undefined);

    // Aggregate FIDO2 devices if needed
    if (fidoAggregation) {
        const fidoDevices = devices.filter(d => d.type === "FIDO2");
        if (fidoDevices.length > 0) {
            // Create the aggregated FIDO device by using deviceTypes.AGGREGATED_FIDO properties
            const aggregatedFidoDevice = {
                ...fidoDevices[0], // Keep the first FIDO device's properties
                displayText: deviceTypes.AGGREGATED_FIDO.nickname, // Use predefined nickname for aggregation
                displayIcon: deviceTypes.AGGREGATED_FIDO.icon, // Use predefined icon for aggregation
                displayType: deviceTypes.AGGREGATED_FIDO.title, // Use predefined title for aggregation
                type: "FIDO2", // Keep the type as FIDO2
                id: fidoDevices[0].id, // Use the ID from the first FIDO2 device
                status: deviceTypes.AGGREGATED_FIDO.status, // Use the Status for Aggregated Devices

                // Add extra info about the number of aggregated FIDO devices
                extraInfo: `${fidoDevices.length} Passkeys`, // Show the number of FIDO devices aggregated
                isAggregated: true,
            };

            // Remove all FIDO2 devices and add the aggregated device
            devices = devices.filter(d => d.type !== "FIDO2");
            devices.push(aggregatedFidoDevice);
        }
        console.log("Device List", devices)
    }

    // Determine how many devices to count for UI/selection
    const count = params.deviceSelection === "DEFAULT_TO_FIRST"
        ? 1
        : devices.length;

    // Create a map of unmasked devices by ID for fast lookup
    const unmaskedMap = new Map();
    (params.unmaskedDevices || []).forEach(d => unmaskedMap.set(d.id, d));

    // ----------------------------------------------------------------------------
    // Enrich devices with displayText, masked/unmasked values, icon, and title
    // ----------------------------------------------------------------------------
    for (const d of devices) {
        if (d.isAggregated) {
            continue;
        }

        const unmasked = unmaskedMap.get(d.id);

        // Populate masked and unmasked values for SMS/VOICE and EMAIL types
        if (d.type === SMS || d.type === VOICE) {
            d.maskedValue = maskPhone(d.phone);
            if (unmasked?.phone) {
                d.phone = unmasked.phone;
            }
        } else if (d.type === EMAIL) {
            d.maskedValue = maskEmail(d.email);
            if (unmasked?.email) {
                d.email = unmasked.email;
            }
        }

        // Adjust type for special case where nickname explicitly signals "Magic Link"
        if (d.nickname === MAGIC) {
            d.type = "MAGIC";
        }

        const nickname = d.nickname || "";
        const maskedValue = d.maskedValue || "";

        // Choose value to display (masked or unmasked) based on flag and type
        const valueToDisplay =
            d.type === SMS || d.type === VOICE
                ? (showUnmasked ? d.phone : d.maskedValue)
                : d.type === EMAIL
                    ? (showUnmasked ? d.email : d.maskedValue)
                    : (showUnmasked ? d.nickname : d.maskedValue || d.nickname || "");

        // Build user-facing displayText
        if (nickname && valueToDisplay && nickname !== valueToDisplay) {
            d.displayText = `${nickname}${SEPARATOR}${valueToDisplay}`;
        } else if (nickname) {
            d.displayText = nickname;
        } else {
            d.displayText = valueToDisplay;
        }

        // Attach icon and title based on device type
        d.displayIcon = deviceTypes[d.type]?.icon || "";
        d.displayType = deviceTypes[d.type]?.title || "Unknown";
        d.status = deviceTypes[d.type]?.status || "OTP_REQUIRED";
    }

    // Determine selectedDeviceId based on conditions
    let finalSelectedDeviceId = null;
    if (fidoAggregation) {
        // If there are only FIDO devices, leave selectedDeviceId as null
        if (devices.every(d => d.type === "FIDO2")) {
            finalSelectedDeviceId = null;
        } else if (selectedDeviceId) {
            finalSelectedDeviceId = selectedDeviceId; // Use the passed selectedDeviceId
        } else if (params.deviceSelection === "DEFAULT_TO_FIRST") {
            finalSelectedDeviceId = null; // Default to null for the first device
        }
    } else {
        finalSelectedDeviceId = selectedDeviceId || null;
    }

    // Return final structured list for the UI
    return {
        usableDevices: devices,
        count: count,
        canChangeDevice: devices.length > 1,
        askToRegister: shouldAskToRegister(devices, allowedTypes),
        selectedDeviceId: finalSelectedDeviceId
    };
};

// ----------------------------------------------------------------------------
// Helper: Determine whether user should be asked to register more devices
// ----------------------------------------------------------------------------
function shouldAskToRegister(devices, allowedTypes) {
    if (!allowedTypes || allowedTypes.length <= 1) return false;

    const filteredTypes = allowedTypes.filter(type => type !== "MFA" && type !== "ALL");

    if (filteredTypes.length <= 1 && filteredTypes.includes("EMAIL")) return false;

    const hasOnlyEmailDevice = devices.every(d => d.type === "EMAIL");
    const hasMultipleAllowed = filteredTypes.length > 1;

    return hasOnlyEmailDevice && hasMultipleAllowed;
}

