code.value:
module.exports = async ({ params }) => {

	const allowedTypes = JSON.parse(params.allowedBackUpTypes.replace(/'/g, '"'));
	const extractedInfo = extractUserInfo(params.userObject, allowedTypes);

	const devices = allowedTypes
		.map(type => {
			const value = extractedInfo[type];
			if (!value || value === "Not Available") return null;
			return buildDevice(type, value);
		})
		.filter(Boolean); // remove nulls

	if (devices.length === 0) {
		return false;
	}

	return { devices };
};

const deviceTypes = {
	SMS: { title: "Text Message", icon: "mdi-comment-text-outline", nickname: "Mobile", description: "Receive a text message with a passcode to the mobile device on file.", maxDevices: -1 },
	VOICE: { title: "Voice", icon: "mdi-face-agent", nickname: "Voice", description: "Receive a phone call with a passcode to the mobile device on file.", maxDevices: -1 },
	EMAIL: { title: "Email", icon: "mdi-email-outline", nickname: "Email", description: "Receive an email with a passcode to the email device on file.", maxDevices: -1 },
	P2P: { title: "Peer-to-Peer", icon: "mdi-account-multiple", nickname: "Peer-to-Peer", description: "Send a one-time passcode to your manager for verification.", maxDevices: 1 }
};


const SEPARATOR = " - ";

// Mask email
const maskEmail = (email = "") => {
	const [username, domain] = email.split("@");
	if (!username || !domain) return "********";
	const masked = username.charAt(0) + "*".repeat(Math.max(0, username.length - 2)) + username.slice(-1);
	return `${masked}@${domain}`;
};

// Mask phone
const maskPhone = (phone = "") => {
	const numeric = phone.replace(/[^\d+]/g, "");
	return `***-***-${numeric.slice(-4)}`;
};

function buildDevice(type, rawValue) {
	const base = deviceTypes[type] || {};
	const device = {
		type,
		id: type,
		status: "ACTIVE",
		usableStatus: { status: "ENABLED" },
		displayType: base.title || "Unknown",
		displayIcon: base.icon || "",
		nickname: base.nickname || "",
		description: base.description || "",
		maxDevices: base.maxDevices ?? -1, // default to -1 if undefined
	};

	if (type === "SMS" || type === "VOICE") {
		device.phone = rawValue;
		device.maskedValue = maskPhone(device.phone);
		device.displayText = `${device.nickname}${SEPARATOR}${device.maskedValue}`;
	} else if (type === "EMAIL") {
		device.email = rawValue;
		device.maskedValue = maskEmail(device.email);
		device.displayText = `${device.nickname}${SEPARATOR}${device.maskedValue}`;
	} else if (type === "P2P") {
		device.manager = rawValue;
		device.displayText = `${device.nickname}${SEPARATOR}${rawValue}`;
	}

	return device;
}



// Extract only available values from user
function extractUserInfo(user = {}, types = []) {
	const result = {};

	const formatPhone = (phone) => {
		if (!phone) return null;
		const numeric = phone.replace(/[^\d]/g, "");
		if (numeric.length === 10) return `1${numeric}`;
		return numeric.length > 0 ? numeric : null;
	};

	if (types.includes("SMS")) {
		const phone = formatPhone(user.mobilePhone);
		if (phone) result.SMS = phone;
	}

	if (types.includes("EMAIL") && user.email) {
		result.EMAIL = user.email;
	}

	if (types.includes("VOICE")) {
		// You can replace this with another field if VOICE uses a different source
		const voicePhone = formatPhone(user.mobilePhone);
		if (voicePhone) result.VOICE = voicePhone;
	}

	if (types.includes("P2P") && user.manager) {
		// Use email as the destination for OTP
		result.P2P = user.manager;
	}

	return result;
}

