/*
 Update the devices array to include a new properties 
 used when rendering device list.
*/

// Main entry point
module.exports = a = async ({ params }) => {

	// Function to get the ID for a given device type
	function getDeviceId(deviceType) {
		// Find the device with the matching type
		const device = (params.listOfDevices).find(d => d.type === deviceType);

		// Return the entire device object if found, otherwise return a not found message
		if (device) {
			return device;
		} else {
			return { message: 'Device type not found' };
		}
	}

	const friendlyTimestamp = new Date().toLocaleString('en-US', {
		month: 'short',   // "Aug"
		day: 'numeric',   // 4
		year: 'numeric',  // 2025
		hour: '2-digit',  // 02
		minute: '2-digit',// 35
		hour12: true     // 12-hour format
	});
	// Result: "Aug 4, 2025, 2:35"


	return {
		"backUpInfo": getDeviceId(params.buttonValue),
		"timeStamp": Math.floor(Date.now() / 1000)
	};
}
