/* 
  This selector class is required as DV
  injects a div into any data-skcomponent="skbutton",
  which alters layout
*/
button > div:first-child {
 display: contents;
}

customScript.value:
// Focus on the first input element on the page
const disableButtons = () => {
    const elements = document.querySelectorAll(".DISABLED_DEVICE");


    elements.forEach(element => {
        element.disabled = true;
        console.log("disabling button ", element.id);
    });

};

// If the page is still loading, wait for it to finish before calling the function
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", disableButtons);
} else {
    // If the page has already finished loading, call the function immediately
    disableButtons();
}

