/**
 * WebAuthn detection variables
 */
let _origin = "";
let _rpId = "";
let _webAuthNSupport = "";

/**
 * Initializes WebAuthn state.
 * Stores results in _origin, _rpId, _webAuthNSupport.
 */
function initializeWebAuthnDetectionAsync() {
    _origin = window.location.origin;
    _rpId = extractRPID(_origin);

    const supported = !!window.PublicKeyCredential;
    if (
        supported &&
        typeof window.PublicKeyCredential.isUserVerifyingPlatformAuthenticatorAvailable === 'function'
    ) {
        return window.PublicKeyCredential.isUserVerifyingPlatformAuthenticatorAvailable()
            .then((available) => {
                _webAuthNSupport = available ? "FULL" : "SECURITY_KEY_ONLY";
            })
            .catch(() => {
                _webAuthNSupport = "SECURITY_KEY_ONLY";
            });
    } else {
        _webAuthNSupport = "NONE";
        return Promise.resolve();
    }
}

function extractRPID(origin) {
    try {
        const hostname = new URL(origin).hostname;
        if (hostname === "localhost" || /^\d{1,3}(\.\d{1,3}){3}$/.test(hostname)) {
            return hostname;
        }
        const parts = hostname.split(".");
        return parts.length < 2 ? hostname : parts.slice(-2).join(".");
    } catch {
        return "unknown";
    }
}

function applyWebAuthnFieldsToForm() {
    const originInput = document.getElementById("origin");
    const rpIdInput = document.getElementById("rpId");
    const supportInput = document.querySelector("[name='webAuthNSupport']");

    if (originInput) originInput.value = _origin;
    if (rpIdInput) rpIdInput.value = _rpId;
    if (supportInput) supportInput.value = _webAuthNSupport;
    // console.log("📋 Applying to form:");
    // console.log(" - origin =", _origin);
    // console.log(" - rpId =", _rpId);
    // console.log(" - webAuthNSupport =", _webAuthNSupport);

    // console.log("🧪 Form input values before submit:");
    // console.log("   origin input =", originInput?.value);
    // console.log("   rpId input =", rpIdInput?.value);
    // console.log("   webAuthNSupport input =", supportInput?.value);
}

/**
 * Sets up a fast-reacting, fail-safe observer to detect payload readiness and click a button.
 * @param {Object} config
 * @param {string} config.targetContainerId - ID of the payload container to watch
 * @param {string} config.buttonId - ID of the continue button to click
 * @param {number} config.timeoutMs - Fallback time to click (default 60s)
 * @param {number} config.retryIntervalMs - Retry interval if button not yet available
 */
function setupSmartPollingWithWebAuthn({
    buttonId = "continueButton",
    timeoutMs = 60000,
    retryIntervalMs = 250
} = {}) {
    initializeWebAuthnDetectionAsync().then(() => {
        const button = document.getElementById(buttonId);
        if (!button) {
            console.warn(`⚠️ Button "${buttonId}" not found.`);
            return;
        }

        const clickIfReady = () => {
            // Simple heuristic: is riskPayload2 available in the global object?
            const form = document.getElementById("pollingForm");
            if (form && form.checkValidity()) {
                applyWebAuthnFieldsToForm();
                console.log("✅ Auto-submitting with risk payload & WebAuthn data.");
                button.click();
                return true;
            }
            return false;
        };

        const retryTimer = setInterval(() => {
            if (clickIfReady()) {
                clearInterval(retryTimer);
                clearTimeout(fallbackTimer);
            }
        }, retryIntervalMs);

        const fallbackTimer = setTimeout(() => {
            if (!clickIfReady()) {
                console.warn("⚠️ Payload not ready after timeout.");
            }
            clearInterval(retryTimer);
        }, timeoutMs);
    });
}

// 🔁 Automatically start on DOM ready
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", () => {
        setupSmartPollingWithWebAuthn();
    });
} else {
    setupSmartPollingWithWebAuthn();
}

