	VOICE: { title: "Voice", icon: "mdi-face-agent", description: "Receive a phone call with a passcode to authenticate.", maxDevices: -1, strength: 1 },
	EMAIL: { title: "Email", icon: "mdi-email-outline", description: "Receive an email with a passcode to authenticate.", maxDevices: -1, strength: 1 },
	TOTP: { title: "Authenticator App", icon: "mdi-cellphone-key", description: "Use an authenticator app to authenticate.", maxDevices: -1, strength: 3 },
	FIDO2: { title: "Passkey", icon: "mdi-fingerprint", description: "Authenticate using the biometrics built into your device.", maxDevices: -1, strength: 4 },
	MOBILE: { title: "Mobile", icon: "mdi-cellphone", description: "Receive a push notification to authenticate.", maxDevices: -1, strength: 2 },
	PLATFORM: { title: "Biometrics", icon: "mdi-fingerprint", description: "Authenticate using the biometrics built into your device.", maxDevices: -1, strength: 4 },
	SECURITY_KEY: { title: "Security Key", icon: "mdi-usb-flash-drive", description: "Authenticate using a hardware security key.", maxDevices: -1, strength: 4 },
	MAGIC: { title: "Magic Link", icon: "mdi-auto-fix", description: "Authenticate by clicking a secure link sent to your email.", maxDevices: -1, strength: 3 },
	OATH_TOKEN: { title: "Hardware Token", icon: "mdi-usb-flash-drive", description: "Authenticate using a physical token that generates one-time passcodes.", maxDevices: -1, strength: 3 },
	YUBIKEY: { title: "YubiKey", icon: "mdi-usb-flash-drive", description: "Authenticate using a YubiKey device that supports tap-to-sign or biometric verification.", maxDevices: -1, strength: 3 },
	PASSWORD: { title: "Password", icon: "mdi-form-textbox-password", description: "Authenticate using a standard password.", maxDevices: 1, strength: 0 }
}

// Device types that require masking
const SMS = "SMS";
const VOICE = "VOICE";
const EMAIL = "EMAIL";
const SEPARATOR = " - ";

// const allowNewIfBlocked = true

// Main entry point
module.exports = a = async ({ params }) => {
	let allowedTypes = params.devices._embedded.allowedtypes || [];
	const allowedDeviceFilterString = params.allowedDeviceFilter;
	const webAuthnSupport = params.webAuthnSupport;
	const existingDevices = params.existingDevices;
	const isAccountRegistration = !!params.isAccountReg; // New input

	// If devices is not passed, head to error branch in flow
	if (!allowedTypes || allowedTypes.length === 0) {
		return false;
	}

	// Optionally filter down from policy-allowed types
	if (!!allowedDeviceFilterString) {
		const allowedDeviceFilter = allowedDeviceFilterString.split(',');
		allowedTypes = allowedTypes.filter(allowedType => allowedDeviceFilter.includes(allowedType));
	}

	// Add password for Account Registration
	if (isAccountRegistration && !allowedTypes.includes("PASSWORD")) {
		allowedTypes.push("PASSWORD");
	}

	let devices = [];

	for (const t of allowedTypes) {
		const typeMeta = deviceTypes[t];
		if (!typeMeta) continue; // skip unknown

		let d = {
			displayText: typeMeta.title || "Unknown",
			displayType: typeMeta.title || `Unknown Device Type: ${t}`,
			displayIcon: typeMeta.icon,
			description: typeMeta.description,
			type: t,
			strength: typeMeta.strength
		};

		devices.push(d);
	}

	// Filter by webAuthn support level
	devices = filterWebAuthnSupportedDevices(webAuthnSupport, devices);

	// Decrease maxDevices count based on already-registered ones
	for (const d of existingDevices) {
		const maxDevices = deviceTypes[d.type]?.maxDevices;
		if (maxDevices !== undefined && maxDevices > 0 && d.lock?.status !== "LOCKED" && d.block?.status !== "BLOCKED") {
			deviceTypes[d.type].maxDevices--;
		}
	}

	// Filter devices based on remaining availability
	devices = devices.filter((d) =>
		deviceTypes[d.type]?.maxDevices === -1 ||
		deviceTypes[d.type]?.maxDevices > 0
	);

	// Return early if none are left
	if (devices.length === 0) {
		return false;
	}

	// Sort by name
	// devices.sort((a, b) => a.displayType.localeCompare(b.displayType));

	// Sort by strength
	devices.sort((a, b) =>
		(deviceTypes[b.type]?.strength ?? -1) - (deviceTypes[a.type]?.strength ?? -1)
	);


	return {
		devices,
		deviceCount: devices.length
	};
};

// Filter webAuthnSupport devices
const filterWebAuthnSupportedDevices = (webAuthnSupport, devices) => {
	if (webAuthnSupport === "NONE") {
		return devices.filter(d =>
			!["SECURITY_KEY", "PLATFORM", "FIDO2", "TOTP", "OTP"].includes(d.type)
		);
	} else if (webAuthnSupport === "SECURITY_KEY_ONLY") {
		return devices.filter(d => d.type !== "PLATFORM");
	}
	return devices;
};

