// ==============================================
// Globals and Configuration
// ==============================================

const pswdPolicy = {{global.company.variables.gv-p1PasswordPolicy}};
let confirmPasswordFocused = false;
const minCharacters = pswdPolicy.minCharacters;


// ==============================================
// Password Rule Generation
// ==============================================

/**
 * Builds and injects password policy rules into the UI.
 */
function generatePasswordRules() {
    const requirementsList = $('#password-requirements');
    requirementsList.empty();

    const rules = [];

    // Length requirement
    if (pswdPolicy.length) {
        rules.push({
            id: "length",
            text: `From ${pswdPolicy.length.min} to ${pswdPolicy.length.max} characters`,
            check: pswdPolicy.length.min > 0
        });
    }

    // Unique characters
    if (pswdPolicy.minUniqueCharacters) {
        rules.push({
            id: "unique",
            text: `${pswdPolicy.minUniqueCharacters} unique characters`,
            check: true
        });
    }

    // Repeating characters
    if (pswdPolicy.maxRepeatedCharacters) {
        rules.push({
            id: "repeating",
            text: `No more than ${pswdPolicy.maxRepeatedCharacters} repeating characters`,
            check: true
        });
    }

    // Must differ from current password
    // if (pswdPolicy.notSimilarToCurrent) {
    //     rules.push({
    //         id: "different",
    //         text: `Must differ from current password`,
    //         check: true
    //     });
    // }

    // Required character types
    for (const [charSet, minCount] of Object.entries(minCharacters)) {
        if (charSet.includes("abcdefghijklmnopqrstuvwxyz")) {
            rules.push({ id: "letter", text: `One lowercase character`, check: true });
        } else if (charSet.includes("ABCDEFGHIJKLMNOPQRSTUVWXYZ")) {
            rules.push({ id: "capital", text: `One UPPERCASE character`, check: true });
        } else if (charSet.includes("0123456789")) {
            rules.push({ id: "number", text: `One number`, check: true });
        } else {
            rules.push({ id: "special", text: `One special character: ${charSet}`, check: true });
        }
    }

    // Inject into the DOM
    rules.forEach(rule => {
        requirementsList.append(
            `<li id="${rule.id}" class="text-dark">
                <i class="mdi mdi-checkbox-blank-circle-outline"></i> ${rule.text}
            </li>`
        );
    });
}


// ==============================================
// Validation Utilities
// ==============================================

/**
 * Updates visual state for a rule in the UI.
 * @param {string} selector - jQuery selector for the rule item.
 * @param {boolean} isValid - Whether the rule is met.
 */
function updateRequirement(selector, isValid) {
    const element = $(selector);
    const originalText = element.text().trim().replace(/<[^>]*>/g, '');

    element.toggleClass("text-success", isValid);
    element.toggleClass("text-dark", !isValid);

    const icon = isValid
        ? '<i class="mdi mdi-check-circle"></i>'
        : '<i class="mdi mdi-checkbox-blank-circle-outline"></i>';

    element.html(`${icon} ${originalText}`);
    return isValid;
}


/**
 * Enables the submit button only if all password requirements are met
 * and new/confirm passwords match.
 */
function enableSubmitButton() {
    const pswd = $('#newPassword').val();
    // const currentPswd = $('#currentPassword').val();
    let allRequirementsMet = true;

    allRequirementsMet &= updateRequirement('#length', pswd.length >= pswdPolicy.length.min);
    allRequirementsMet &= updateRequirement('#unique', new Set(pswd).size >= pswdPolicy.minUniqueCharacters);
    allRequirementsMet &= updateRequirement('#repeating', !/(.)\1{2,}/.test(pswd));
    allRequirementsMet &= updateRequirement('#letter', /[a-z]/.test(pswd));
    allRequirementsMet &= updateRequirement('#capital', /[A-Z]/.test(pswd));
    allRequirementsMet &= updateRequirement('#number', /\d/.test(pswd));
    allRequirementsMet &= updateRequirement('#special', /[^a-zA-Z0-9]/.test(pswd));

    if (pswdPolicy.notSimilarToCurrent && typeof currentPswd !== 'undefined') {
        const different = pswd !== currentPswd && currentPswd.length > 0;
        allRequirementsMet &= updateRequirement('#different', different);
    }


    const confirmPassword = $('#confirmPassword').val();
    const passwordsMatch = pswd === confirmPassword;

    $('#submitBtn').prop('disabled', !(allRequirementsMet && passwordsMatch));
}


/**
 * Validates whether the two password fields match.
 */
function validatePasswords() {
    const newPassword = $('#newPassword').val();
    const confirmPassword = $('#confirmPassword').val();
    const isMatch = newPassword === confirmPassword;

    $('#matchFeedback').toggle(!isMatch);
    $('#confirmPassword').css('border', isMatch ? '1px solid #8b9197' : '1px solid red');
}


// ==============================================
// Password Toggle Control
// ==============================================

/**
 * Adds a show/hide toggle button to a password field container.
 * @param {string} id - Container element ID.
 */
function makePasswordToggle(id) {
    const container = document.getElementById(id);
    if (!container) return;

    const password = container.querySelector("input");
    const toggler = document.createElement("button");
    toggler.setAttribute("type", "button");
    toggler.setAttribute("aria-label", "Show/Hide Password");
    toggler.className = "btn mdi mdi-eye-off-outline position-absolute end-0 top-50 translate-middle-y";
    container.appendChild(toggler);

    toggler.addEventListener("click", () => {
        const isHidden = password.type === "password";
        password.type = isHidden ? "text" : "password";
        toggler.classList.toggle("mdi-eye-outline", isHidden);
        toggler.classList.toggle("mdi-eye-off-outline", !isHidden);
        password.focus();
    });
}


// ==============================================
// Focus Helpers
// ==============================================

/**
 * Focuses on the current password field initially.
 */
const focusOnFirstInputElement = () => {
    document.getElementById("newPassword")?.focus();
};


// ==============================================
// Initialization
// ==============================================

$(document).ready(function () {
    // Generate the rules list
    generatePasswordRules();

    // Wire toggle buttons
    // makePasswordToggle("currentPasswordContainer");
    makePasswordToggle("passwordContainer");
    makePasswordToggle("confirmPasswordContainer");

    // Focus first input
    focusOnFirstInputElement();

    // Tooltip handling
    const passwordField = $('#newPassword');
    const tooltip = $('#password-tooltip');
    tooltip.hide();

    passwordField.on('focus', () => tooltip.fadeIn(200));
    passwordField.on('keyup', () => {
        if (passwordField.val().length > 0) {
            tooltip.show();
        }
        enableSubmitButton();
    });
    passwordField.on('blur', () => {
        if (passwordField.val().length === 0) {
            tooltip.fadeOut(200);
        }
    });

    // Confirm password validation
    $('#confirmPassword')
        .on('focus', () => { confirmPasswordFocused = true; })
        .on('blur', () => { confirmPasswordFocused = false; })
        .on('input', () => {
            if (confirmPasswordFocused) {
                validatePasswords();
                enableSubmitButton();
            }
        });

    // Run initial validation
    enableSubmitButton();
});

