customScript.value:
// ==============================================
// DOM Focus Utilities
// ==============================================

/**
 * Focuses the element with the given ID.
 * @param {string} id - The ID of the DOM element to focus.
 */
const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    } else {
        console.log(`Element with ID '${id}' not found.`);
    }
};


// ==============================================
// Password Toggle UI
// ==============================================

/**
 * Creates a show/hide password toggle for an input inside a given container.
 * @param {string} id - ID of the container that includes the password input.
 */
const makePasswordToggle = (id) => {
    const container = document.getElementById(id);
    if (!container) return;

    const password = container.querySelector("input");
    if (!password) return;

    const toggler = document.createElement("button");
    toggler.setAttribute("type", "button");
    toggler.setAttribute("aria-label", "Show/Hide Password");
    toggler.className = "btn mdi mdi-eye-off-outline position-absolute end-0 top-50 translate-middle-y";
    container.appendChild(toggler);

    /**
     * Toggles visibility of password and icon.
     */
    const togglePasswordVisibility = () => {
        const isHidden = password.type === "password";
        password.type = isHidden ? "text" : "password";
        toggler.classList.toggle("mdi-eye-outline", isHidden);
        toggler.classList.toggle("mdi-eye-off-outline", !isHidden);
        password.focus();
    };

    toggler.addEventListener("click", togglePasswordVisibility);
};


// ==============================================
// Username Input Validation
// ==============================================

/**
 * Enables/disables a button based on Username input validity.
 * @param {string} inputId - ID of the Username input field.
 * @param {string} buttonId - ID of the button to enable/disable.
 */
const toggleButtonState = (inputId, buttonId) => {
    const inputField = document.getElementById(inputId);
    const button = document.getElementById(buttonId);

    if (inputField && button) {
        // Disable the button initially when the DOM is fully loaded
        button.disabled = true;

        // Add input event listener to update the button state dynamically
        inputField.addEventListener("input", () => {
            button.disabled = inputField.value.trim().length === 0;
        });
    } else {
        console.log(`Element with ID ${inputId} or ${buttonId} not found.`);
    }
};


// ==============================================
// Language Selector Submission
// ==============================================

/**
 * Automatically submits form when language selection changes.
 */
const wireLanguageSelector = () => {
    const languageSelector = document.getElementById("language");
    const form = document.getElementById("passwordForm");

    if (languageSelector && form) {
        languageSelector.addEventListener("change", () => form.submit());
    }
};


// ==============================================
// WebAuthn Detection Logic
// ==============================================

let _origin = "";
let _rpId = "";
let _webAuthNSupport = "";

/**
 * Detects if WebAuthn API is supported.
 */
function isWebAuthnSupported() {
    try {
        return !!window.PublicKeyCredential;
    } catch {
        return false;
    }
}

/**
 * Checks if a platform authenticator is available.
 * @returns {Promise<boolean>}
 */
function isWebAuthnPlatformAuthenticatorAvailable() {
    if (
        isWebAuthnSupported() &&
        typeof window.PublicKeyCredential.isUserVerifyingPlatformAuthenticatorAvailable === 'function'
    ) {
        return window.PublicKeyCredential.isUserVerifyingPlatformAuthenticatorAvailable()
            .then(result => result)
            .catch(() => false);
    }
    return Promise.resolve(false);
}

/**
 * Extracts Relying Party ID from origin.
 * @param {string} origin - URL origin.
 * @returns {string} - RP ID.
 */
function extractRPID(origin) {
    try {
        const hostname = new URL(origin).hostname;
        if (hostname === "localhost" || /^\d{1,3}(\.\d{1,3}){3}$/.test(hostname)) return hostname;
        const parts = hostname.split(".");
        return parts.length < 2 ? hostname : parts.slice(-2).join(".");
    } catch {
        return "unknown";
    }
}

/**
 * Populates WebAuthn support fields for form submission.
 */
function applyWebAuthnFieldsToForm() {
    const originInput = document.getElementById("origin");
    const rpIdInput = document.getElementById("rpId");
    // const supportInput = document.querySelector("[name='webAuthNSupport']");
    const supportInput = document.getElementById("webAuthNSupport");

    if (originInput) originInput.value = _origin;
    if (rpIdInput) rpIdInput.value = _rpId;
    if (supportInput) supportInput.value = _webAuthNSupport;
}

/**
 * Sets up detection of WebAuthn support and availability.
 */
function initializeWebAuthnDetection() {
    _origin = window.location.origin;
    _rpId = extractRPID(_origin);

    const supported = isWebAuthnSupported();

    isWebAuthnPlatformAuthenticatorAvailable().then((available) => {
        _webAuthNSupport = available
            ? "FULL"
            : (supported ? "SECURITY_KEY_ONLY" : "NONE");
    }).catch(() => {
        _webAuthNSupport = supported ? "SECURITY_KEY_ONLY" : "NONE";
    });
}

/**
 * Binds WebAuthn field injection to form submit button.
 * @param {string} buttonId - ID of the submit button.
 */
function wireWebAuthnFormSubmit(buttonId = "btnContinue") {
    const button = document.getElementById(buttonId);
    if (button) {
        button.addEventListener("click", applyWebAuthnFieldsToForm);
    }
}


// ==============================================
// App Initialization
// ==============================================

/**
 * Initializes event wiring, detection, and form state.
 */
const start = () => {
    makePasswordToggle("passwordDiv");
    setFieldFocus("username");
    initializeWebAuthnDetection();
    wireWebAuthnFormSubmit("btnContinue");
    wireWebAuthnFormSubmit("btnForgot");
    wireWebAuthnFormSubmit("btnBack");
    toggleButtonState("username", "btnContinue");
};


// ==============================================
// DOM Ready Check
// ==============================================

if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", start);
    const submitButton = document.getElementById("btnContinue");
    if (submitButton) submitButton.disabled = true;
} else {
    start();
    const submitButton = document.getElementById("btnContinue");
    if (submitButton) submitButton.disabled = true;
}

