         * updates the toggler button's icon, and refocuses on the password field.
         */
        const showHidePassword = () => {
            if (password.type === "password") {
                password.type = "text";
                toggler.classList.add("mdi-eye-outline");
                toggler.classList.remove("mdi-eye-off-outline");
            } else {
                password.type = "password";
                toggler.classList.add("mdi-eye-off-outline");
                toggler.classList.remove("mdi-eye-outline");
            }
            password.focus();
        };

        toggler.addEventListener("click", showHidePassword);
    }
};

/**
 * Initializes the application, specifically setting up password visibility toggling.
 * This should be called when the DOM is fully loaded.
 */
const start = () => {
    makePasswordToggle("passwordDiv");
    setFieldFocus("password");
};

// Add event listener to ensure the script runs after the DOM is fully loaded
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", start);
} else {
    start();
}

