// Write your code here
// Supported language: Javascript 
module.exports = a = async ({ params }) => {
	const arr = params.deviceList
	if (Array.isArray(arr) && arr.length === 0) {
		return false;
	} else {
		return;
	}
}

// Titles and icons
const deviceTypes = {
    SMS: { title: "Text Message", icon: "mdi-comment-text-outline" },
    VOICE: { title: "Voice", icon: "mdi-face-agent" },
    EMAIL: { title: "Email", icon: "mdi-email-outline" },
    TOTP: { title: "Authenticator App", icon: "mdi-cellphone-key" },
    FIDO2: { title: "Passkey", icon: "mdi-fingerprint" },
    PLATFORM: { title: "Biometrics", icon: "mdi-fingerprint" },
    MOBILE: { title: "Mobile Push", icon: "mdi-account-key-outline" },
    PINGID_MOBILE: { title: "PingID App", icon: "mdi-cellphone" },
    SECURITY_KEY: { title: "Security Key", icon: "mdi-fingerprint" },
    MAGIC: { title: "Magic Link", icon: "mdi-auto-fix" },
    PINGID_DESKTOP: { title: "Desktop App", icon: "mdi-auto-fix" },
    YUBIKEY: { title: "YubiKey", icon: "mdi-usb-flash-drive" }
};

// Device types that require masking
const SMS = "SMS";
const VOICE = "VOICE";
const EMAIL = "EMAIL";
const SEPARATOR = " - ";
const MAGIC = "Magic Link";

// Mask email
const maskEmail = (email) => {
    const [username, domain] = email.split("@");
    const maskedUsername =
        username.charAt(0) + "*".repeat(username.length - 2) + username.slice(-1);
    return maskedUsername + "@" + domain;
};

// Mask phone number
const maskPhone = (phone) => {
    const numericPhoneNumber = phone.replace(/[^\d+]/g, "");
    // Mask the first six digits of the phone number
    const maskedPhoneNumber = "***-***-" + numericPhoneNumber.slice(-4);
    return maskedPhoneNumber;
};

// Main entry point
module.exports = async ({ params }) => {
    console.log('params: ', params);

    let devices = params.devices;
    const passwordlessAuth = params.passwordlessAuth;
    const allowedTypes = params.passwordlessAllowedTypes || [];

    // If devices is not passed or is empty, return an empty array
    if (!devices || devices.length === 0) {
        return { usableDevices: [], count: 0, canChangeDevice: false };
    }

    // Filter devices based on passwordless authentication
    if (passwordlessAuth === true) {
        devices = devices.filter(d => allowedTypes.includes(d.type));
        if (devices.length === 0) {
            return { usableDevices: [], count: 0, canChangeDevice: false }; // No valid devices
        }
    }

    // Apply webAuthenSupport filters
    if (params.webAuthNSupport === "NONE") {
        devices = devices.filter(device => {
            return device.type !== "SECURITY_KEY" && device.type !== "PLATFORM";
        });
    }

    if (params.webAuthNSupport === "SECURITY_KEY_ONLY") {
        devices = devices.filter(device => device.type !== "PLATFORM");
    }

    devices = devices.filter(device => device.coolDownExpiresAt == undefined);

    // Determine the count based on deviceSelection
    let count;
    if (params.deviceSelection === "DEFAULT_TO_FIRST") {
        count = 1; // Set count to 1 if deviceSelection is "DEFAULT_TO_FIRST"
    } else {
        count = devices.length; // Otherwise, use the length of devices
    }

    // Process devices to include additional properties
    for (const d of devices) {
        if (d.type === SMS || d.type === VOICE) {
            d.maskedValue = maskPhone(d.phone);
        } else if (d.type === EMAIL) {
            d.maskedValue = maskEmail(d.email);
        }
        const nickname = d.nickname || "";
        const maskedValue = d.maskedValue || "";

        if (d.nickname === MAGIC) {
            d.type = "MAGIC";
        }

        if (nickname && maskedValue) {
            d.displayText = `${nickname}${SEPARATOR}${maskedValue}`;
        } else if (!nickname && !maskedValue) {
            d.displayText = "";
        } else {
            d.displayText = `${nickname}${maskedValue}`;
        }

        // Set the icon and type
        d.displayIcon = deviceTypes[d.type]?.icon || "";
        d.displayType = deviceTypes[d.type]?.title || "Unknown";
    }

    return {
        usableDevices: devices,
        count: devices.length,
        canChangeDevice: devices.length > 1
    };
};

